Select customer.CustomerID,customer.CustomerName,orders.OrderID,orders.TotalCost,menu.MenuName,menuitems.CourseName,menuitems.StarterName from customer INNER JOIN orders ON customer.CustomerID = orders.CustomerID INNER JOIN menu ON orders.MenuID = menu.MenuID INNER JOIN menuitems ON menu.MenuItemsID = menuitems.MenuItemID where orders.TotalCost > 150 ORDER BY orders.TotalCost ASC;